import base64
import zlib

# Step 1: Base64 encoded string (shortened for clarity — use the full string in your code)
b64_data = ''''''  # (use full string here)

# Fix base64 padding
b64_data += '=' * (-len(b64_data) % 4)

# Decode base64
decoded = base64.b64decode(b64_data)

# Remove custom wrapper bytes
trimmed = decoded[10:-8]

# Decompress and save to file
try:
    output = zlib.decompress(trimmed, -zlib.MAX_WBITS)
    with open("decrypted.php", "w", encoding="utf-8", errors="ignore") as f:
        f.write(output.decode('utf-8', errors='ignore'))
    print("✅ Decrypted PHP code saved to 'decrypted.php'")
except Exception as e:
    print("❌ Decompression failed:", e)